import React, {useState, useEffect} from 'react';
import './App.css';
import axios from 'axios';
import Button from '@material-ui/core/Button'
import Container from '@material-ui/core/Container'
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import { makeStyles } from '@material-ui/styles';

const styles = makeStyles(theme => ({
    mainContent: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: '25rem'
    }
}))

function App() {
    const useStyles = styles()

    const getTheStuff = (url) =>
        axios.get()
            .then(res => {
                const text = res.data;
                setState({text: text, upper: state.upper})
            })
    const [state, setState] = useState({text: "placeholder", upper: false})
    useEffect(() => {
        const url = state.upper ? 'http://localhost:8080/upper' : 'http://localhost:8080/'
        console.log(url)
        getTheStuff(url)
    }, [])

    const clickHandler = (old) => {
        setState({text: state.text, upper: !old})
        const url = state.upper ? 'http://localhost:8080/upper' : 'http://localhost:8080/'
        console.log(url)
        getTheStuff(url)
    }

    return (
        <Container className={useStyles.mainContent} maxWidth="sm">
            <Card>
                <CardContent>{state.text}</CardContent>
                <CardActions>
                    <Button onClick="clickHandler(state.upper)" size="small">Smoll</Button>
                </CardActions>
            </Card>
        </Container>
    )
}

export default App
