import React, {useState, useEffect} from 'react';
import './App.css';
import axios from 'axios';
import Button from '@material-ui/core/Button'
import Container from '@material-ui/core/Container'
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import { makeStyles } from '@material-ui/styles';

const styles = makeStyles(theme => ({
    mainContent: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        height: '25rem'
    }
}))

function App() {
    const [state, setState] = useState({text: "placeholder", upper: false})
    const useStyles = styles()
    const updateCase = (upper) => {
        const url = upper ? 'http://localhost:8080/upper' : 'http://localhost:8080/'
        axios.get(url)
            .then(res => {
                const text = res.data;
                setState({text: text, upper: upper})
            })
    }
    const clickHandler = () => {
        console.log("click")
        updateCase(!state.upper)//flip the case setting
    }
    useEffect(() => {
        console.log("effect")
        updateCase(state.upper)
    }, [])

    return (
        <Container className={useStyles.mainContent} maxWidth="sm">
            <Card>
                <CardContent>{state.text}</CardContent>
                <CardActions>
                    <Button onClick={clickHandler} size="small">Smoll</Button>
                </CardActions>
            </Card>
        </Container>
    )
}

export default App
